mapper.select:
SELECT * FROM MEMBER
WHERE MEMBER_STATUS = 'Y'
AND MEMBER_EMAIL = #{memberEmail}

SELECT COUNT(*) FROM MEMBER
WHERE MEMBER_STATUS = 'Y'
AND MEMBER_EMAIL = #{memberEmail}

mapper.update:
UPDATE MEMBER SET  
MEMBER_EMAIL = #{memberEmail},
MEMBER_PHONE = #{memberPhone},
MEMBER_ADDR = #{memberAddress}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET  
MEMBER_PW = #{memberPw}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET  
MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}
